objects.pattern:
rule DAMASCENED_PEACOCK_resolve_apis {
    meta:
        author = "NCSC"
        description = "Targets a code pattern which is used to resolve APIs in the first stage which loads DAMASCENED PEACOCK."
        hash1 = "1bd5b9fec7ccc29144989203c3b15a10c9c22a76"

    strings:
	    $ = {56 31 C0 85 D2 74 1D 0F B6 31 41 4A 69 F6 [4] 0F AF F6 C1 C6 10 31 C6 0F AF F6 C1 C6 10 89 F0 EB DF 5E C3}
    condition:
	    (uint16(0) == 0x5A4D) and uint32(uint32(0x3C)) == 0x00004550 and all of them
}

rule DAMASCENED_PEACOCK_winhttpconnect {
    meta:
        author = "NCSC"
        description = "This rule targets a code pattern in DAMASCENED PEACOCK which sees the resolved pointer for WinHttpConnect retrieved from a struct, arguments pushed e.g. 8080 as the port, and then a call."
        hash1 = "8c25b982e5e4b3418b779c85b8ac8a6acf393063"

    strings:
        $ = {8B 85 90 00 00 00 6A 00 68 90 1F 00 00 FF 76 34 FF 33 FF D0}
    condition:
        (uint16(0) == 0x5A4D) and uint32(uint32(0x3C)) == 0x00004550 and all of them
}

rule DAMASCENED_PEACOCK_API_hashes {
    meta:
        author = "NCSC"
        description = "This rule targets code patterns for pushing the hash onto the stack and then moving the returned pointer into a structure in DAMASCENED PEACOCK."
        hash1 = "8c25b982e5e4b3418b779c85b8ac8a6acf393063"

    strings:
        $ = {68 1A 81 A8 01} // push hash
        $ = {83 C4 04 89 46 04} // move result into struct

        $ = {68 7D 53 AE 52}
        $ = {83 C4 04 89 46 08}

        $ = {68 59 6F 3C 00}
        $ = {83 C4 04 89 46 0C}

        $ = {68 6B 9A C6 E1}
        $ = {83 C4 04 89 46 10}

        $ = {68 32 A9 7C 08}
        $ = {83 C4 04 89 46 14}

        $ = {68 76 EE 30 8A}
        $ = {83 C4 04 89 46 18}

    condition:
        (uint16(0) == 0x5A4D) and uint32(uint32(0x3C)) == 0x00004550 and all of them

}

rule DAMASCENED_PEACOCK_API_hashing_algorithm {
    meta:
        author = "NCSC"
        description = "This rule targets the code in DAMASCENED PEACOCK used for generating an API hash. The rule is agnostic of the constants used to generate the hash."
        hash1 = "8c25b982e5e4b3418b779c85b8ac8a6acf393063"

    strings:
        $ = {8D0C0669C9[4]8D5201C1C9??81C1[4]03C1C1C8??0FAFC60FBE3203C08BC8C1C8??C1C9??85F6}
    condition:
        any of them
}
