rule rp_junk_code { 
    meta:
        author = "NCSC"
        description = "Highlights common junk code sections identified in both Rhythmic Parry samples."
        date = "2022-01-10"
        hash1 = "628cf9ace06af1d0985fce3c5c39ab0773135c00"
        hash2 = "af895002b93854de0eb6f17dc4823a50c7bd08f5"
    strings: 
	$post_writefile = {C7 84 24 ?? ?? 00 00 00 00 00 00 48 8B 84 24 ?? ?? 00 00 48 89 8C 24 ?? ?? 00 00 48 89 C1 48 8D 05 ?? ?? ?? 00 89 94 24 ?? ?? 00 00 48 89 C2 41 B8}  
	$temp_path_a = {41 B8 C8 00 00 00 89 8C 24 ?? ?? 00 00 44 89 C1} 
    condition: 
	uint16(0) == 0x5A4D and uint32(uint32(0x3C)) == 0x00004550 and filesize > 500KB and (#temp_path_a > 30) and (#post_writefile > 12)
}

rule rp_hash_payload { 
    meta:
        author = "NCSC"
        description = "Hash comparison routine used to check downloaded payload in Rhythmic Parry samples."
        date = "2022-01-10"
        hash1 = "628cf9ace06af1d0985fce3c5c39ab0773135c00"
        hash2 = "af895002b93854de0eb6f17dc4823a50c7bd08f5"
    strings: 
	$hash_1 = {0F BE ?? ?? 48 8B 44 24 ?? ?? 63 ?? 24 ?? ?? 0F BE}  
        $hash_2 = {0F 84 08 00 00 00 C7 44 24 ?? 00 00 00 00 E9 00 00 00 00 8B 44 24 ?? 83 C0 01 89 44 24 ?? E9 ?? ?? FF FF}  
	$hash_3 = {C7 44 24 ?? 01 00 00 00 48 8B ?? 24 [1-5] 48 89 44 24 ?? 48 83 [3-6] 00 0F 85 0D 00 00 00 C7 44 24 ?? 00 00 00 00 E9} 
 
    condition: 
	    (uint16(0) == 0x5A4D) and uint32(uint32(0x3C)) == 0x00004550 and all of them 
}

rule rp_xor_routine { 
    meta:
        author = "NCSC"
        description = "Targets the XOR routine used to decode configuration strings in both observed Rhythmic Parry samples."
        date = "2022-01-10"
        hash1 = "628cf9ace06af1d0985fce3c5c39ab0773135c00"
        hash2 = "af895002b93854de0eb6f17dc4823a50c7bd08f5"
    strings: 
	$xor_1 = {48 8B ?? 24 ?? [0-3] 48 63 ?? 24 ?? [0-3] 0F B? 14 ?8 48 63 ?? 24}  
        $xor_2 = {45 31 C0 89 54 24 ?? 44 89 C2 48 F7 ?? 24 ?? [0-3] 44 (0F BE 84 14|8A 8C)} 
        $xor_3 = {00 00 66 45 89 C1 45 0F B7 C1 44 8B 54 24 ?? 45 31 C2 66 45 89 D1} 
    condition: 
	     (uint16(0) == 0x5A4D) and uint32(uint32(0x3C)) == 0x00004550 and 2 of them 
}
